package com.tistory.comfy91.excuseme_android.feature.unlockimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport android.view.Viewimport android.view.WindowManagerimport androidx.core.content.ContextCompatimport androidx.core.widget.doOnTextChangedimport com.tistory.comfy91.excuseme_android.Rimport kotlinx.android.synthetic.main.activity_unlock.*import com.tistory.comfy91.excuseme_android.setOnSingleClickListenerclass UnlockActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(com.tistory.comfy91.excuseme_android.R.layout.activity_unlock)        window.setSoftInputMode(WindowManager.LayoutParams.SOFT_INPUT_ADJUST_RESIZE or WindowManager.LayoutParams.SOFT_INPUT_STATE_ALWAYS_HIDDEN)        initUi()        edt_unlockpw.doOnTextChanged { text1, start, count, after ->            if (!text1.isNullOrBlank()) {                btnUnlockGoHelper.background = ContextCompat.getDrawable(this, R.drawable.bg_btn_pink)            } else {                btnUnlockGoHelper.background = ContextCompat.getDrawable(this, R.drawable.bg_btn_gray)            }        }    }    private fun initUi(){        btnUnlockBack.setOnSingleClickListener {            finish()        }        btnUnlockGoHelper.setOnSingleClickListener{            //todo            // 다음으로 갈 액티비티 지정            //this.newStartActivity()        }    }}